   v1   =   atoi ( buf ); 
   if   (   v1     0   ||   v1   =   count   ) 
   { 
     puts ( Out of bound! ); 
     _exit ( 0 ); 
   } 
   if   (   notelist [ v1 ]   ) 
     notelist [ v1 ] - put ( notelist [ v1 ]); 
   return   __readgsdword ( 0x14u )   ^   v3 ;  }

